$.fn.getPost = function(){
    var burl;
    var post_id;
    burl    = $('#baseurl').val();
    post_id = $('#post_id').val();                
    $.ajax({
        url: burl + '/wp-json/wp/v2/posts/' + post_id,
        method: 'GET',            
        crossDomain: true,
        contentType: 'application/json',
        beforeSend: function() { $('#loading').show(); },            
        success: function( data ) {
            console.log( data );                
            var output  = JSON.stringify( data );
            var op      = $.parseJSON(output);
            $('#title').val(op.title.rendered);
            CKEDITOR.instances['description'].setData(op.content.rendered);
            //alert(output);
        },
        complete: function() { $('#loading').hide(); },
        error: function( error ) {
            console.log( error );
            alert("Invalid Post ID or not exists");
            $("#post_id").focus();
        }
    });
}